import classes from "./Cart.module.css";
import Modal from "../UI/Modal";
import Card from "../UI/Card";

const Cart=(props)=>{
    const cartItem=<ul className={classes.list}>
        {[{ id: 'c1', name: 'Sushi', amount: 2, price: 12.99 }].map((item) => (
            <li className={classes.meal}>
                <div>
                    <div className={classes.name}>{item.name}</div>
                    <div className={classes.price}>${item.price.toFixed(2)}</div>
                </div>
                <div className={classes["btn-group"]}>
                    <button className={classes.btn}>-</button>
                    <button className={classes.btn}>+</button>
                </div>
            </li>
        ))}</ul>
    return(
        <Modal onHideCart={props.onHideCart}>
            <Card>
                {cartItem}
                <div className={classes.total}>
                    <div className={classes["total-amount"]}>
                        <span className={classes.title}>Total Amount</span>
                        <span className={classes["total-price"]}>$32.57</span>
                    </div>
                    <div className={classes["btn-group1"]}>
                        <button className={classes["close-btn"]} onClick={props.onHideCart}>Close</button>
                        <button className={classes["order-btn"]}>Order</button>
                    </div>
                </div>
            </Card>
        </Modal>
    );
};
export default Cart;
