import React,{useContext,useRef} from "react";

import classes from "./MealsItemForm.module.css";
import Input from "../../UI/Input";
import CartContext from "../../../context-store/CartContext1";

const MealsItemForm=(props)=>{

    const amountInputRef=useRef();

    const ctxObj=useContext(CartContext);

    const handleSubmit=(event)=>{
        event.preventDefault();

        const amountNum=+amountInputRef.current.value;

        onAddToCart(amountNum);

    };

    return(
        <form className={classes.form} onSubmit={handleSubmit}>
            <Input ref={amountInputRef} label="Amount" inputObj={{type:"number", id:props.id, min:"1", max:"5", defaultValue:"1",step:"1"}} />
            <button className={classes.btn}>+ Add</button>
        </form>
    );
};
export default MealsItemForm;
