import CartContext from "./CartContext1";
import React,{useReducer} from "react";

const cartObject={
    items:[],
    totalAmount:0
};

const cartReducer=(state,action)=>{
    if(action.type==="ADD"){
        const upadtedItems=state.items.concat(action.item);
        const updatedTotalAmount=state.totalAmount=state.totalAmount+action.item.amount*action.item.price;
        return {items:upadtedItems, totalAmount:updatedTotalAmount};
    }
    return cartObject;
};

const CartContextProvider=(props)=>{

    const [cartState,dispatchCart]=useReducer(cartReducer,cartObject);

    const handleAddItemToCart=(item)=>{
        dispatchCart({type:"ADD", item:item});
    };

    const handleRemoveItemFromCart=(id)=>{
        dispatchCart({type:"REMOVE", id:id});
    };

    const contextObj={
        items:cartObject.items,
        totalAmount:cartObject.totalAmount,
        addItem:handleAddItemToCart,
        removeItem:handleRemoveItemFromCart
    };

    return(
        <CartContext.Provider value={contextObj}>
            {props.children}
        </CartContext.Provider>
    );
};
export default CartContextProvider;
